import { Component, Input, OnInit } from '@angular/core';
import { NavigationService } from '../services/navigation.service';
import { UtilityService } from '../services/utility.service';
import { Cart, Payment, PaymentMethod, Product, User } from '../models/models';
import { Subject } from 'rxjs';
import jsPDF from 'jspdf';

import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
import { TDocumentDefinitions } from 'pdfmake/interfaces';
import { FormControl } from '@angular/forms';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

class Invoice {
  user: User | any;
  products: Product[] = [];
}


@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrl: './cart.component.css'
})
export class CartComponent implements OnInit {

  usersCart: Cart = {
    id: 0,
    user: this.utilityService.getUser(),
    cartItems: [],
    ordered: false,
    orderedOn: '',
  };

  usersPaymentInfo: Payment = {
    id: 0,
    user: this.utilityService.getUser(),
    paymentMethod: {
      id: 0,
      type: '',
      provider: '',
      available: false,
      reason: '',
    },
    totalAmount: 0,
    shipingCharges: 0,
    amountReduced: 0,
    amountPaid: 0,
    createdAt: '',
  };

  usersPreviousCarts: Cart[] = [];

  product: Product = {
    id: 0,
    title: '',
    description: '',
    price: 0,
    quantity: 0,
    productCategory: {
      id: 0,
      category: '',
      subCategory: '',
    },
    offer: {
      id: 0,
      title: '',
      discount: 0,
    },
    imageName: '',
  };


  constructor(public utilityService: UtilityService, private navigationService: NavigationService) {

  }

  ngOnInit(): void {
    // Get Cart 
    this.navigationService.getActiveCartOfUser(this.utilityService.getUser().id).subscribe((res: any) => {
      this.usersCart = res;

      // Calculate Payment
      this.utilityService.calculatePayment(this.usersCart, this.usersPaymentInfo);
    });

    //Get Previous Carts 
    // this.navigationService.getAllPreviousCarts(this.utilityService.getUser().id).subscribe((res: any) => {
    //   this.usersPreviousCarts = res;
    // });

    this.previousCarts();
  }

  previousCarts() {
    this.navigationService.getAllPreviousCarts(this.utilityService.getUser().id).subscribe((res: any) => {
      this.usersPreviousCarts = res;

      // Sort the array based on the orderedOn date of usersCart
      this.usersPreviousCarts.sort((a, b) => {
        // Convert orderedOn strings to Date objects
        const dateA = new Date(a.orderedOn);
        const dateB = new Date(b.orderedOn);

        // Sort based on the orderedOn date
        return dateB.getTime() - dateA.getTime();
      });
    });
  }


  invoice = new Invoice();
  generatePDF(action = 'download', cartId: number) {
    const user: User = this.utilityService.getUser();
    const usercart = this.usersCart;
    const userprecart = this.usersPreviousCarts;
    const userpayment = this.usersPaymentInfo;
    const preCart = this.previousCarts();
    let printContents = document.getElementById('printTable' + cartId)!.cloneNode(true) as HTMLElement;

    const cart = userprecart.find(cart => cart.id === cartId);

    if (!cart) {
      console.error('Error: Cart not found');
      return;
    }


    try {
      const docDefinition: TDocumentDefinitions = {
        content: [
        {
          stack: [
            { image: "data:image/png;base64,", "fit": [70,70], alignment: 'left'},
            {
              text: 'E-ArtiJewel',
              fontSize: 16,
              alignment: 'center',
              color: 'black',
              bold: true
            },
          ]
          
        },
          {
            margin: [0, 10, 0, 10], // Add top and bottom margins of 15 units
            text: '', // Add an empty text element to create space
          },
          {
            text: 'INVOICE',
            fontSize: 20,
            bold: true,
            alignment: 'center',
            decoration:'underline',
            color: 'gold',
          },
          {
            text: 'Customer Details',
            style: 'sectionHeader'
          },
          {
            columns: [
              [
                {
                  text:`${user.firstName} ${user.lastName}`, // Assuming you want to include both first name and last name
                  bold: true
                },
                { text: user.address },
                { text: user.email },
                { text: user.mobile },
              ],
              [
                {
                  text: `Date: ${new Date().toLocaleString()}`,
                  alignment: 'right'
                },
                {
                  text: `Bill No : ${((Math.random() * 1000).toFixed(0))}`,
                  alignment: 'right'
                }
              ]
            ]
          },
          {
            text: 'Order Details',
            style: 'sectionHeader'
          },
          {
            table: {
              headerRows: 1,
              widths: ['auto','*', 'auto', 'auto'], 
              body: [
                [
                  { text: 'No.', bold: true, fillColor: 'yellow' },
                  { text: 'Product Name', bold: true, fillColor: 'yellow' },
                  { text: 'OrderedOn', bold: true, fillColor: 'yellow' },
                  { text: 'Price', bold: true, fillColor: 'yellow' }],
                ...cart.cartItems.map((item, index) => ([
                  { text: index + 1 },
                  item.product.title,
                  cart.orderedOn, 
                  item.product.price,
              ])),
                [{text: 'Total Items :- ' + cart.cartItems.length, colSpan:2, bold: true, fillColor: 'yellow', alignment:'left'},{},
                { text: 'Total Amount :-  ' + this.utilityService.calculatePricePaid(cart), colSpan: 2, bold: true, fillColor: 'yellow', alignment: 'right' }, {}]
              ]
            }
          },
          {
            margin: [0, 15, 0, 15], // Add top and bottom margins of 15 units
            text: '', // Add an empty text element to create space
          },
          {
            columns: [
              [{ qr: `${user.firstName}`, "fit": 90 }], 
              [{ image: "data:image/png;base64, ", alignment: 'right', "fit": [200, 200] }]
            ],
          },
          {
            margin: [0, 15, 0, 15], // Add top and bottom margins of 15 units
            text: '', // Add an empty text element to create space
          },
          {
            text: 'Terms and Conditions',
            style: 'sectionHeader'
          },
          {
            ul: [
              'Order can be return in max 10 days.',
              'The Toal Amount is with the discounted price and Shipping Charges.',
              'Warranty of the product will be subject to the manufacturer terms and conditions.',
              'This is system generated invoice.',
            ],
          }
        ],
        styles: {
          sectionHeader: {
            bold: true,
            decoration: 'underline',
            fontSize: 14,
            margin: [0, 15, 0, 15],
          }
        }
      };

      if (action == 'download') {
      pdfMake.createPdf(docDefinition).download('Invoice.pdf');
      } if ( action == 'open') {
        pdfMake.createPdf(docDefinition).open()
      }
    }
    catch (error) {
      console.error('Error:', error)
    }
  }
}

